%% Función Polinomial:%% Genera una matriz que contiene los precios calculados para cada %% orden polinomial indicado a través del vector "orden".## Entradas:## 'X' son los datos normalizados.## 'Y' son las respectivas etiquetas de los datos.## 'nareas' son las áreas linealmente espaciadas y normalizadas que se utilizarán##                    para evaluar la hipótesis.## 'np' es el normalizador.## 'method' es el método de descenso de gradiente que se desea evaluar.## 'orden' es un vector que contiene los órdenes polinomiales a evaluar.## Salidas:## 'precios' es una matriz que contiene los precios calculados con la hipótesis ## para un valor de orden específico. Cada columna es un orden distinto.function precios=polinomial(X,Y,nareas,np,method,orden)  ## Thetas disponibles hasta un orden polinomial de n=10  t0=[-1 -0.2 -0.3 0.1 -0.4 0.6 0.2 1 0.33 -0.57 0];  precios = [];    if (strcmp(method,"batch") == 1)    for n=orden      [thetas,~]=descentpoly(@loss,@gradloss,t0(:,1:n+1),X,Y,0.004,                            "method","batch","epsilon",0.005,"maxiter",300);      A = hypothesis(nareas',thetas(end,:));      precios = [precios np.itransform(A)];    endfor   endif    if (strcmp(method,"stochastic") == 1)    for n=orden      [thetas,~]=descentpoly(@loss,@gradloss,t0(:,1:n+1),X,Y,0.001,                            "method","stochastic","epsilon",0.001,"maxiter",500,                            "minibatch",20);      A = hypothesis(nareas',thetas(end,:));      precios = [precios np.itransform(A)];    endfor   endif    if (strcmp(method,"momentum") == 1)    for n=orden      [thetas,~]=descentpoly(@loss,@gradloss,t0(:,1:n+1),X,Y,0.003,                            "method","momentum","epsilon",0.001,"maxiter",500,                            "minibatch",20);      A = hypothesis(nareas',thetas(end,:));      precios = [precios np.itransform(A)];    endfor   endif    if (strcmp(method,"rmsprop") == 1)    for n=orden      [thetas,~]=descentpoly(@loss,@gradloss,t0(:,1:n+1),X,Y,0.01,                            "method","rmsprop","epsilon",0.005,"maxiter",500,                            "beta",0.9,"beta2",0.95,"minibatch",20);      A = hypothesis(nareas',thetas(end,:));      precios = [precios np.itransform(A)];    endfor   endif    if (strcmp(method,"adam") == 1)    for n=orden      [thetas,~]=descentpoly(@loss,@gradloss,t0(:,1:n+1),X,Y,0.05,                            "method","adam","epsilon",0.005,"maxiter",500,                            "beta",0.9,"minibatch",20);      A = hypothesis(nareas',thetas(end,:));      precios = [precios np.itransform(A)];    endfor   endif  endfunction;  